define("UPFLOOR", "(");
define("DOWNFLOOR", ")");


function part1($input){

	$floor = 0;

	$inputArray = str_split($input);

	foreach ($inputArray as $character) {

		if ($character == UPFLOOR){
			$floor++;
		}
		else if ($character == DOWNFLOOR){
			$floor--;
		}
		else{
			echo "Bad input";
			break;
		}
	}

	return $floor;

}

function part2($input){
	$count = 1;

	$inputArray = str_split($input);

	foreach ($inputArray as $character) {

		if ($character == UPFLOOR){
			$floor++;
		}
		else if ($character == DOWNFLOOR){
			$floor--;
		}
		else{
			echo "Bad input";
			break;
		}

		if ($floor == -1){
			return $count;
		}
		else{
			$count++;
		}

	}


}

echo part1($input);
echo part2($input);

